<script>
	let isSaldoHidden = true;
	const saldoElement = document.getElementById('saldo');
	const toggleButton = document.getElementById('toggle-saldo');

	function toggleSaldo() {
		if (isSaldoHidden) {
			saldoElement.textContent = 'Rp 1.000.000'; // Ganti ini dengan nominal saldo asli
			toggleButton.textContent = '👁️';
		} else {
			saldoElement.textContent = 'Rp ***********';
			toggleButton.textContent = '🙈';
		}
		isSaldoHidden = !isSaldoHidden;
	}

</script>
    
